import React, { Component } from 'react';
import ReactSearchBox from 'react-search-box';
import './SearchBar.css';
import Fuse from 'fuse.js';

export default class SearchBar extends Component {
  constructor (props) {
    super (props);
    this.state = {
      data: [
        {
          key: 'john',
          value: 'John Doe',
        },
        {
          key: 'jane',
          value: 'Jane Doe',
        },
        {
          key: 'mary',
          value: 'Mary Phillips',
        },
        {
          key: 'robert',
          value: 'Robert',
        },
        {
          key: 'karius',
          value: 'Karius',
        },
      ]
    }
  }

  handleSearch (record) {
    let options = {
      shouldSort: true,
      threshold: 0,
      location: 0,
      distance: 100,
      minMatchCharLength: 1,
      keys: [
          "key",
          "value"
      ]
  };

  let fuse = new Fuse(this.state.data, options); // "list" is the item array
        let result = fuse.search(record);
        console.log(result);
  }

  render() {
    return (
        <div className = "reactSearchBox">
            <ReactSearchBox
                placeholder="Search"
                inputBoxFontSize = "1.2em"
                data={this.state.data}
                onSelect={record => this.handleSearch.bind(this,record)}
            />
      </div>
    )
  }
}
