import React, {Component} from 'react';
import './VidComponent.css'
import { NavLink } from 'react-router-dom';
import { AppContext } from '../context/AppContext';

class VidComponent extends Component {
    constructor(props){
        super(props);
    }

    render() {
        return (
            <AppContext.Consumer>
                <div className = "vid-row">
                    {this.props.source?this.props.source.map(video=>{
                        return(
                            <div className = "video">
                                <NavLink to="/VidPlayPage" activeStyle={{textDecoration:'none'}}>
                                    <video width = "350" controls muted autoPlay src={video.item.url} />
                                </NavLink>
                                <NavLink to="/VidPlayPage">
                                    <p>{video.item.title}</p>
                                </NavLink>
                            </div>
                        )
                    }):''}
                </div>
            </AppContext.Consumer>
        );
    }
}

export default VidComponent;
