import React, { Component } from 'react';
import InputField from './InputField';
import UserStore from '../stores/UserStore';
import SubmitButton from './SubmitButton';
import './LoginForm.css';
import logo from '../images/logo.png'


class LoginForm extends Component {

    constructor(props) {
        super(props);
        this.state = {
            username: '',
            password: '',
            // buttonDisabled: false,
        }
    }

    setInputValue(property, val) {
        val = val.trim();
        if (val.length > 12) {
            return;
        }
        this.setState({
            [property]: val
        })
    }

    resetForm() {
        this.setState({
            username: '',
            password: '',
            // buttonDisabled: false
        })
    }

    doLogin() {
        if (!this.state.username) {
            return;
        }
        if (!this.state.password) {
            return;
        }

        // this.setState({
        //     buttonDisabled: true
        // })

        try {
            const {username, password} = this.state;
            let formdata = new FormData;
            formData.append("username", username);
            formData.append("password", password);
            const url = 'http://localhost:80/react-backend/login.php';
            axios.post(url,formData).then(res =>{
                console.log(res.data);
                if (res.data==='ok'){
                    UserStore.isLoggedIn = true;
                    this.props.auth();
                }
            }).catch(err => console.log(err));
            axios.get(url).then(res =>{console.log(res)}).catch(err => console.log(err));
        }
        catch(e) {
            console.log(e);
            this.resetForm();
        }
    }




    render() {

        return (
            <div className = "loginForm">
                <div className = "signin_title">
                    <img className="title_logo" src={logo} alt="logo"/>
                    <p>Sign in</p>
                    <p>for more features</p>
                </div>
                <div className = "input_container">
                    <label>Username: </label>
                    <InputField 
                        type = 'text'
                        placehoder = 'Username'
                        value = {this.state.username ? this.state.username : ''}
                        onChange = {(val) => this.setInputValue('username', val)}
                    />
                </div>

                <div className = "input_container">
                    <label>Password: </label>
                    <InputField 
                        type = 'password'
                        placehoder = 'Password'
                        value = {this.state.password ? this.state.password : ''}
                        onChange = {(val) => this.setInputValue('password', val)}
                    />
                </div>

                <div className = "btn_row">
                    <SubmitButton 
                        text = 'Register'
                        disabled = {this.state.buttonDisabled}
                        onClick = {this.props.showSignup}
                    />
                    <SubmitButton 
                        text = 'Login'
                        disabled = {this.state.buttonDisabled}
                        onClick = {this.doLogin}
                    />
                </div>
            </div>

        );
    }
}

export default LoginForm;
