import React, {Component} from 'react';
import logo from '../images/logo.png'
import './NavBar.css'
import MenuButton from "./HamburgerButton/HamburgerButton";
import ToggleNav from "./ToggleNav/ToggleNav";


class NavBar extends Component {
    constructor(props){
        super(props);
        this.state = {
            open:false,
        };
    }

    handleClick=()=>{
        this.setState((prevState)=>{
            return {open:!prevState.open}
        })
    };

    render() {
        let toggleNav;
        if (this.state.open) {
            toggleNav = <ToggleNav/>
        }
        return (
            <div className="newNav">
                <nav className="nav_bar">
                    <div className="logo_container">
                        <img className="vids_logo" src={logo} alt="logo"/>
                        <label>VIDS</label>
                    </div>
                    <div className="menuBtn">
                        <MenuButton open={this.state.open} onClick={this.handleClick}/>
                    </div>

                </nav>
                <div className="toggleNavContainer">{toggleNav}</div>
            </div>
        );
    }
}

export default NavBar;
