import React, { Component } from 'react';
import InputField from './InputField';
import UserStore from '../stores/UserStore';
import SubmitButton from './SubmitButton';
import './LoginForm.css';
import logo from '../images/logo.png'
import SigninForm from './SigninForm';


class LoginForm extends Component {

    constructor(props) {
        super(props);
        this.state = {
            username: '',
            password: '',
            buttonDisabled: false,

        }
    }

    setInputValue(property, val) {
        val = val.trim();
        if (val.length > 12) {
            return;
        }
        this.setState({
            [property]: val
        })
    }

    resetForm() {
        this.setState({
            username: '',
            password: '',
            buttonDisabled: false
        })
    }

    async doLogin() {
        if (!this.state.username) {
            return;
        }
        if (!this.state.password) {
            return;
        }

        this.setState({
            buttonDisabled: true
        })

        try {
            let res = await fetch('/login', {
                method: 'post',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    username: this.state.username,
                    password: this.state.password
                })
            });

            let result = await res.json();
            if (result && result.success) {
                UserStore.isLoggedIn = true;
                UserStore.username = result.username;
            }
            else if (result && result.success === false) {
                this.resetForm();
                alert(result.msg);
            }
        }
        catch(e) {
            console.log(e);
            this.resetForm();
        }
    }




    render() {

        let currentShowingPageComponent;

        const {
          currentShowingPage,
        } = this.state;

        if (currentShowingPage === 0) {
          currentShowingPageComponent = <LoginForm />;
        } else if (currentShowingPage === 1) {
          currentShowingPageComponent = <SigninForm />;
        }



        return (
            <div className = "loginForm">
                <div className = "signin_title">
                    <img className="title_logo" src={logo} alt="logo"/>
                    <p>Sign in</p>
                    <p>for more features</p>
                </div>
                <div className = "input_container">
                    <label>Username: </label>
                    <InputField 
                        type = 'text'
                        placehoder = 'Username'
                        value = {this.state.username ? this.state.username : ''}
                        onChange = {(val) => this.setInputValue('username', val)}
                    />
                </div>

                <div className = "input_container">
                    <label>Password: </label>
                    <InputField 
                        type = 'password'
                        placehoder = 'Password'
                        value = {this.state.password ? this.state.password : ''}
                        onChange = {(val) => this.setInputValue('password', val)}
                    />
                </div>

                <div className = "btn_row">
                    <SubmitButton 
                        text = 'Register'
                        disabled = {this.state.buttonDisabled}
                        onClick = {() =>
                            this.setState({currentShowingPage: 1})}
                    />
                    <SubmitButton 
                        text = 'Login'
                        disabled = {this.state.buttonDisabled}
                        onClick = {() => this.doLogin()}
                    />
                </div>
            </div>

        );
    }
}

export default LoginForm;
