import React, {Component} from 'react';
import './UploadPage.css'
import ReactS3 from 'react-s3';

const config = {
    bucketName: 'wis7202',
    // dirName: 'photos', /* optional */
    region: 'ap-southeast-2',
    accessKeyId: 'AKIAJBYV56D767PX3H2Q',
    secretAccessKey: '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',
};


class UploadPage extends Component {

    upload = (e) => {
        let upload_video = e.target.files[0];
        ReactS3.uploadFile(upload_video,config)
            .then((data) => {
                alert('loading succeed');
                this.setState({buttonDisabled:false,video_url:data.location});
            })
            .catch((err) => {console.log(err)});
    }

    render() {
        return(
            <div className = "uploadPage">
                <div className = "uploadBox">
                    <span>Please click Select File <br/> OR <br/>Drag your file here to upload</span>
                    <a class="uploadBtn">
                        Select File
                        <input class="change" type="file" multiple="multiple" onChange = {this.upload}/>
                    </a>          
                    <input id='showFileName' type='text' readonly />
                </div>

                <label>Title</label>
                <input className = "titleBox" placeholder = "Add a title for your video..."/>

                <label>Description</label>
                <textarea className = "descriptionBox" placeholder = "Add descriptions for your video..."/>

                <button className = "descriptionUpload">Upload</button>
            </div>
        )
    }
}

export default UploadPage;
