import React, { Component } from 'react';
import ReactSearchBox from 'react-search-box';
import './SearchBar.css';

export default class SearchBar extends Component {
  data = [
    {
      key: 'john',
      value: 'John Doe',
    },
    {
      key: 'jane',
      value: 'Jane Doe',
    },
    {
      key: 'mary',
      value: 'Mary Phillips',
    },
    {
      key: 'robert',
      value: 'Robert',
    },
    {
      key: 'karius',
      value: 'Karius',
    },
  ]

  handleSearch = () => {
    let options = {
      shouldSort: true,
      threshold: 0,
      location: 0,
      distance: 100,
      minMatchCharLength: 1,
      keys: [
          "key",
          "value"
      ]
  };

  let fuse = new Fuse(data, options); // "list" is the item array
        let result = fuse.search(keyword);
        console.log(result);
  }

  render() {
    return (
        <div className = "reactSearchBox">
            <ReactSearchBox
                placeholder="Search"
                inputBoxFontSize = "1.2em"
                data={this.data}
                onSelect={record => this.handleSearch}
            />
      </div>
    )
  }
}
