import React, {Component} from 'react';
import './UploadPage.css'
import ReactS3 from 'react-s3';
import axios from 'axios';

const config = {
    bucketName: 'wis7202',
    // dirName: 'photos', /* optional */
    region: 'ap-southeast-2',
    accessKeyId: 'AKIAJBYV56D767PX3H2Q',
    secretAccessKey: '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',
};


class UploadPage extends Component {

    constructor(props) {
        super(props);
        this.state = {
            video_url:'',
            buttonDisabled:true,
        }
    }


    upload = (e) => {
        let upload_video = e.target.files[0];
        console.log(upload_video);
        ReactS3.uploadFile(upload_video,config)
            .then((data) => {
                alert('loading succeed');
                this.setState({buttonDisabled:false,video_url:data.location});
                document.getElementById('ShowFileName').innerText = upload_video.name;
            })
            .catch((err) => {console.log(err)});

    }

    handleSubmit=(e) => {
        e.preventDefault();
        let description = e.target.description.value;
        let title = e.target.title.value;
        console.log(description);
        // send the video url to php
        const url = 'http://localhost:80/react-backend/upload.php';
        const formData = new FormData;
        formData.append("title", title);
        formData.append("video_upload", this.state.video_url);
        formData.append("description", description);
        axios.post(url,formData).then((res)=>{console.log(res)}).catch((err)=>console.log(err));
    };

    render() {
        return(
            <form className = "uploadPage" onSubmit={this.handleSubmit}>
                <div className = "uploadBox">
                    <span>Please click Select File <br/> OR <br/>Drag your file here to upload</span>
                    <a class="uploadBtn">
                        Select File
                        <input class="change" accept = "video/mp4" type="file" multiple="multiple" onChange = {this.upload}/>
                    </a>          
                    <p id='showFileName'>uploaded file</p>
                </div>

                <label>Title</label>
                <input className = "titleBox" placeholder = "Add a title for your video..." name = "title"/>

                <label>Description</label>
                <textarea className = "descriptionBox" placeholder = "Add descriptions for your video..." name = "description"/>

                <input type="submit" value="Upload" className = "descriptionUpload" disabled = {this.state.buttonDisabled}/>
            </form>
        )
    }
}

export default UploadPage;
