import React, {Component} from 'react';
import CommentInput from '../components/CommentInput'
import CommentList from '../components/CommentList'

class VidPlayPage extends Component {

    constructor () {
        super()
        this.state = {
          comments: []
        }
      }

    handleSubmitComment (comment) {
        if (!comment.content) return alert('The comments cannot be empty! Please enter your comments.')
        this.state.comments.push(comment)
        this.setState({
          comments: this.state.comments
        })      
    }

    render(){
        return (
            <div className = "vidPlayPage">
                <div className = "vidNComments">
                    <div className = "vidPlayWindow">
                        <video width = "800" height = "450" controls muted autoPlay src = "https://wis7202.s3-ap-southeast-2.amazonaws.com/vid_lemon.mp4" />
                    </div>
                    <div className = "commentsArea">
                        <CommentInput onSubmit={this.handleSubmitComment.bind(this)} />
                        <CommentList comments={this.state.comments} />
                    </div>
                </div>
                <div className = "vidsList">
                    <ul>
                        <video width = "230" height = "150" src = "https://wis7202.s3-ap-southeast-2.amazonaws.com/vid_sky.mp4" />
                        <video width = "230" height = "150" src = "https://wis7202.s3-ap-southeast-2.amazonaws.com/vid_sky.mp4" />

                    </ul>
                </div>

                <script>
                    $(document).ready(function() {       
        $('.video').each(function(i, obj) {
            $(this).on("mouseover", function() { hoverVideo(i); });
            $(this).on("mouseout", function() { hideVideo(i); });
        })
});

        function hoverVideo(i) {  
                $('.thevideo')[i].play()
        }

        function hideVideo(i) {
                $('.thevideo')[i].currentTime = 0,
                $('.thevideo')[i].pause()
        }
        </script>

            </div>
        );
    }
}

export default VidPlayPage;
