import React, { Component } from 'react';
import Fuse from 'fuse.js';
import VidComponent from "./VidComponent";
import './SearchBar.css';

class Search extends Component {
    constructor(props) {
        super(props);
        this.state = {
            list: [
                {
                    title: "Old Man's War",
                    author: {
                        firstName: "John",
                        lastName: "Scalzi"
                    },
                    url: "https://wis7202.s3-ap-southeast-2.amazonaws.com/vid_bird.mp4"
                },
                {
                    title: "The Lock Artist",
                    author: {
                        firstName: "Steve",
                        lastName: "Hamilton"
                    },
                    url : "https://wis7202.s3-ap-southeast-2.amazonaws.com/vid_grass.mp4"
                },
                {
                    title: "HTML5",
                    author: {
                        firstName: "Remy",
                        lastName: "Sharp"
                    },
                    url: "https://wis7202.s3-ap-southeast-2.amazonaws.com/vid_raining.mp4"
                }
            ],
            result: undefined
        }
    }

    handleSearch=(e)=>{
        console.log(e.target.search.value);
        e.preventDefault();
        let options = {
            shouldSort: true,
            threshold: 0,
            location: 0,
            distance: 100,
            minMatchCharLength: 1,
            keys: [
                "title"
            ]
        };
        let fuse = new Fuse(this.state.list, options); // "list" is the item array
        this.setState({result: fuse.search(e.target.search.value)},()=>{
            console.log(this.state.result);
        });
        if(e.target.search.value==''){
          document.getElementsByClassName("vidsDisplay").style.display = "block"
        }else{
          document.getElementsByClassName("vidsDisplay").style.display = "none"
        }

    };
    render() {
        return (
            <div className = "searchContainer">
                <form onSubmit={this.handleSearch}>
                    <input name='search' type="text"/>
                    <button>search</button>
                </form>
                <div className="videoArea">
                    <VidComponent source={this.state.result}/>

                    <div className = "vidsDisplay">
                      <div className = "vidsContainer">
                        <div className = "vidsRow">
                              <div className = "videoContainer">
                              <iframe width="400" height="225" src="https://wis7202.s3-ap-southeast-2.amazonaws.com/vid_bird.mp4" frameborder="0" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>
                              <p>Description</p>
                              </div>

                              <div className = "videoContainer">
                              <iframe width="400" height="225" src="https://wis7202.s3-ap-southeast-2.amazonaws.com/vid_grass.mp4" frameborder="0" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>
                              <p>Description</p>
                              </div>

                              <div className = "videoContainer">
                              <iframe width="400" height="225" src="https://wis7202.s3-ap-southeast-2.amazonaws.com/vid_raining.mp4" frameborder="0" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>
                              <p>Description</p>
                              </div>
                        </div>
                      </div>
                    </div>


                </div>
            </div>
        );
    }
}

export default Search;
