import React, {Component, useState} from 'react';
import axios from 'axios';

function InputBox(){
        const [state,setState]=useState({
                text:'',

        });
        const handleAdd= async (e)=>{
                await setState({text:e.target.value})
        };


        const handleSubmit = (e)=>{
                e.preventDefault();
                console.log(state.text);
                let formData = new FormData;
                formData.append("text", state.text);
                const url = 'http://localhost:80/react-backend/';
                axios.post(url,formData).then(res =>{console.log(res)}).catch(err => console.log(err));
                axios.get(url).then(res =>{console.log(res)}).catch(err => console.log(err));
        };
        return (
            <div>
                <input onChange={handleAdd} value={state.text} type="text" id="text" placeholder='enter some text'/>
                <button onClick={handleSubmit} id='submit'>save</button>
            </div>
        );
}

export default InputBox;
