import React, { Component } from 'react';
import './SearchBar.css';
import Fuse from 'fuse.js';

class Search extends Component {
    handleSearch=(e)=>{
        console.log(e.target.search.value);
        e.preventDefault();
        let list=[
            {
                title: "Old Man's War",
                author: {
                    firstName: "John",
                    lastName: "Scalzi"
                },
                url: "https://wis7202.s3-ap-southeast-2.amazonaws.com/vid_bird.mp4"
            },
            {
                title: "The Lock Artist",
                author: {
                    firstName: "Steve",
                    lastName: "Hamilton"
                },
                url : "https://wis7202.s3-ap-southeast-2.amazonaws.com/vid_grass.mp4"
            },
            {
                title: "HTML5",
                author: {
                    firstName: "Remy",
                    lastName: "Sharp"
                },
                url: "https://wis7202.s3-ap-southeast-2.amazonaws.com/vid_raining.mp4"
            }
        ];
        let options = {
            shouldSort: true,
            threshold: 0,
            location: 0,
            distance: 100,
            minMatchCharLength: 1,
            keys: [
                "title",
                "author.firstName"
            ]
        };
        let fuse = new Fuse(list, options); // "list" is the item array
        let result = fuse.search(e.target.search.value);
        console.log(result);
    };
    render() {
        return (
            <div>
                <form onSubmit={this.handleSearch}>
                    <input name='search' type="text"/>
                    <button>search</button>
                </form>
            </div>
        );
    }
}

export default Search;
