import React, {useState, useEffect, useContext} from 'react';
import axios from 'axios';
import { AppContext } from '../context/AppContext';
import VidComponent from '../components/VidComponent'

function MyProfilePage() {

    const [state, setState] = useState({
        myVids: '',
        new_myVids: undefined

    });

    const {currentUser} = useContext(AppContext);

    useEffect(() => {
        const url = 'http://localhost:80/react-backend/profile.php';
        const formData = new FormData;
        formData.append('uploader', currentUser);
        axios.post(url, formData).then((res)=>{
            console.log(res);
            setState({myVids: res.data.video})
        })
        .then(()=>{
            const new_myVids=state.myVids.map(a=>({item:a}));
            console.log(new_myVids);
            console.log(state.myVids);
            setState({new_myVids:new_myVids})
        })
        .catch((err)=>console.log(err));
        console.log(state.myVids);


    }, [])

    return(
        <div>
            <p>hahahah</p>
            <VidComponent source={state.new_myVids}/>
        </div>
    );

}

export default MyProfilePage;
