import React, { Component } from 'react';
import Fuse from 'fuse.js';
import VidComponent from "./VidComponent";
import './SearchBar.css';

class Search extends Component {
    constructor(props) {
        super(props);
        this.state = {
            list: [
                {
                    title: "Old Man's War",
                    author: {
                        firstName: "John",
                        lastName: "Scalzi"
                    },
                    url: "https://wis7202.s3-ap-southeast-2.amazonaws.com/vid_bird.mp4"
                },
                {
                    title: "The Lock Artist",
                    author: {
                        firstName: "Steve",
                        lastName: "Hamilton"
                    },
                    url : "https://wis7202.s3-ap-southeast-2.amazonaws.com/vid_grass.mp4"
                },
                {
                    title: "HTML5",
                    author: {
                        firstName: "Remy",
                        lastName: "Sharp"
                    },
                    url: "https://wis7202.s3-ap-southeast-2.amazonaws.com/vid_raining.mp4"
                },
                {
                  title: "HTML5",
                  author: {
                      firstName: "Remy",
                      lastName: "Sharp"
                  },
                  url: "https://wis7202.s3-ap-southeast-2.amazonaws.com/vid_lemon.mp4"
              },
              {
                title: "HTML5",
                author: {
                    firstName: "Remy",
                    lastName: "Sharp"
                },
                url: "https://wis7202.s3-ap-southeast-2.amazonaws.com/vid_wine.mp4"
            },
            {
              title: "HTML5",
              author: {
                  firstName: "Remy",
                  lastName: "Sharp"
              },
              url: "https://wis7202.s3-ap-southeast-2.amazonaws.com/vid_sky.mp4"
          }
            ],
            result: undefined,
            new_list: undefined,
            keyword: ''
        }
    };

    componentDidMount() {
      const new_list=this.state.list.map(a=>({item:a}));
      console.log(new_list);
      console.log(this.state.result);
      this.setState({new_list:new_list})
  }


    handleSearch=(e)=>{
        console.log(e.target.search.value);
        e.preventDefault();
        let options = {
            shouldSort: true,
            threshold: 0.5,
            location: 0,
            distance: 100,
            minMatchCharLength: 1,
            keys: [
                "title"
            ]
        };
        let fuse = new Fuse(this.state.list, options); // "list" is the item array
        this.setState({
            result: fuse.search(e.target.search.value),
            keyword: e.target.search.value
        });
    };


    render() {
        return (
            <div className = "searchContainer">
                <form onSubmit={this.handleSearch}>
                    <input name='search' type="text" placeholder = "Search..."/>
                    <button></button>
                </form>
                <div className="videoArea">
                {this.state.keyword !== '' ? <VidComponent source={this.state.result}/> : <VidComponent source={this.state.new_list}/>}
                </div>
            </div>
        );
    }
}

export default Search;
